      let emailIdPattern = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
      let emailInput = input.value;
      if (!emailInput.match(emailIdPattern))
        errorMsgsCollection.push('The Email Id is invalid');
